#include <iostream> #include <vector> #include <deque> using namespace std; vector<int> firstNegativeNumber(vector<int>& arr, int K) { vector<int> result; deque<int> negatives; for (int i = 0; i < arr.size(); i++) { if (arr[i] < 0) negatives.push_back(i); if (!negatives.empty() && negatives.front() < i - K + 1) negatives.pop_front(); if (i >= K - 1) result.push_back(negatives.empty() ? 0 : arr[negatives.front()]); } return result;